        category: 'Validaciones',
      },
    },
    // Eventos
    valueChange: {
      action: 'valueChange',
      description: 'Evento emitido cuando cambia el valor del textarea',
      table: {
        type: { summary: 'EventEmitter<string>' },
        category: 'Eventos',
