{
  title: 'Typografia/Heading',
  component: SaHeadingComponent,
  parameters: {
    layout: 'centered',
    docs: {
      source: {
        type: 'dynamic',
        language: 'html',
        transform: (code: string, storyContext: any) => {
          // Transformaci√≥n simple que preserva el resaltado de sintaxis
          let result = code;
          
          // Transformar property bindings innecesarios a attribute binding
          result = result.replace(/\[text\]="'([^']+)'"/g, 'text="$1"');
          result = result.replace(/\[size\]="'([^']+)'"/g, 'size="$1"');
          result = result.replace(/\[weight\]="'([^']+)'"/g, 'weight="$1"');
          result = result.replace(/\[mt\]="'([^']+)'"/g, 'mt="$1"');
          result = result.replace(/\[mb\]="'([^']+)'"/g, 'mb="$1"');
          result = result.replace(/\[mr\]="'([^']+)'"/g, 'mr="$1"');
          result = result.replace(/\[ml\]="'([^']+)'"/g, 'ml="$1"');
          
          // Limpiar espacios extra
          result = result.replace(/\n\s*\n/g, '\n');
          
          return result;
        }
      }
    }
  },
  tags: ['autodocs'],
  argTypes: {
