          <sa-input
            label="Detector de teclas"
            placeholder="Escribe aqu√≠ para ver los eventos"
            [(ngModel)]="mensaje"
            (change)="onChange($event)"
            (keyup)="ultimoEvento = onKeyEvent('KeyUp', $event)"
