  parameters: {
    docs: {
      description: {
        story: `Ejemplo de uso con @ViewChild y template reference variables. Muestra c√≥mo acceder al componente sa-input desde el c√≥digo TypeScript.

## üìñ Documentaci√≥n de uso:

### HTML Template
\`\`\`html
<sa-input 
  #focusPacientes
  label="Campo con referencia"
  placeholder="Input con template reference"
  [(ngModel)]="mensaje">
</sa-input>
\`\`\`

### TypeScript Component
\`\`\`typescript
@ViewChild('focusPacientes') focusPacientes!: SaInputComponent;

// M√©todos disponibles:
this.focusPacientes.focusInput();
this.focusPacientes.selectAll();
const nativeInput = this.focusPacientes.getNativeInput();
\`\`\`
        `
      },
      source: {
        language: 'typescript',
        code: `
// HTML Template
<sa-input 
  #focusPacientes
  label="Campo con referencia"
  placeholder="Input con template reference"
  [(ngModel)]="mensaje">
</sa-input>

<button (click)="enfocarInput()">Enfocar</button>

// TypeScript Component
import { Component, ViewChild, ElementRef } from '@angular/core';
import { SaInputComponent } from './sa-input.component';

@Component({...})
export class MiComponente {
  @ViewChild('focusPacientes') focusPacientes!: SaInputComponent;

  // Tambi√©n puedes acceder al elemento HTML nativo directamente:
  @ViewChild('focusPacientes', { read: ElementRef }) focusPacientesElement!: ElementRef<HTMLElement>;

  mensaje: string = '';

  enfocarInput() {
    // Opci√≥n 1: Usar el m√©todo p√∫blico del componente (recomendado)
    this.focusPacientes.focusInput();

    // Opci√≥n 2: Acceder al elemento nativo directamente
    const nativeInput = this.focusPacientes.getNativeInput();
    if (nativeInput) {
      nativeInput.focus();
    }
  }

  seleccionarTodo() {
    this.focusPacientes.selectAll();
  }

  obtenerValor() {
    return this.focusPacientes.value;
  }
}`
      }
    }
  }
}

