</sa-calendar>

<!-- 4. Rango específico (próxima semana) -->
<sa-calendar 
  [minDate]="nextWeekStart"
  [maxDate]="nextWeekEnd"
  label="Fecha de cita"
  placeholder="Solo próxima semana"
  helperText="Del {{ nextWeekStart | date:'dd/MM/yyyy' }} al {{ nextWeekEnd | date:'dd/MM/yyyy' }}">
</sa-calendar>`
      }
    }
  }
