  [minDate]="minHistoricalDate"
  label="Fecha del reporte"
  placeholder="Últimos 2 años"
  helperText="Datos disponibles desde {{ minHistoricalDate | date:'dd/MM/yyyy' }}">
</sa-calendar>`
      }
    }
  }
}

